import React, { Component } from 'react';
import Slider from "react-slick";
import authors from '../../../data/authors.json';

class Testmonials extends Component {
    render() {
        const settings = {
            slidesToShow: 3,
            slidesToScroll: 1,
            fade: false,
            infinite: true,
            autoplay: true,
            autoplaySpeed: 4000,
            arrows: false,
            dots: true,
            responsive: [
                {
                    breakpoint: 992,
                    settings: {
                        slidesToShow: 2,
                    },
                },
                {
                    breakpoint: 576,
                    settings: {
                        slidesToShow: 1,
                    },
                },
            ],
        };
        return (
            <section className="testimonial-section pb-115 pt-115">
                <div className="container">
                    <div className="section-title text-center mb-80">
                        <span className="title-tag">testimonials</span>
                        <h2>Client Feedback</h2>
                    </div>
                    {/* testimonials loop  */}
                    <Slider className="row testimonial-slider" {...settings}>
                        {authors.map((item, i) => (
                            <div key={i} className="col-lg-12">
                                <div className="testimonial-box">
                                    <div className="client-img">
                                        <img src={process.env.PUBLIC_URL + "/" + item.img} alt={item.name} />
                                        <span className="check"><i className="fal fa-check" /></span>
                                    </div>
                                    <h3>{item.name}</h3>
                                    <span className="clinet-post">{item.designation}</span>
                                    <p>{item.shotdesc}</p>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </section>
        );
    }
}

export default Testmonials;
