import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Link } from "react-router-dom";
import { Button } from "semantic-ui-react";

class mainProduct extends Component {
  styles = {
    margin: "10px",
  };
  state = {
    products: ["product 1", "product 2", "product 3"],
    selectedproduct: "select a product",
    active: 2,
  };
  changedroplist = (e) => {
    this.setState({ selectedproduct: e.target.value });
  };
  click(val) {
    console.log(val);
    this.setState({ active: val });
  }
  handleClick = (e) => {
    e.preventDefault();
    e.target.style.border = "5px solid blue";
  };
  render() {
    return (
      <section className="contact-part pt-115 pb-115">
        <div className="container">
          <div className="contact-form">
            <div
              style={{ textAlign: "center", width: "500px", margin: "0 auto" }}
            >
              <select onChange={this.changedroplist} id="mainProduct">
                <option value="select a product">Select a product</option>
                {this.state.products.map((products) => (
                  <option value={products}>{products}</option>
                ))}
              </select>

              <h3 style={{ margin: "20px" }}>{this.state.selectedproduct}</h3>

              <select id="No.people">
                <option value="">Select the number of people</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
              </select>

              <input
                style={{ margin: "20px", position: "relative", right: "20px" }}
                type="date"
                id="date"
              />

              <button
                style={this.styles}
                className=""
                onClick={() => this.click(1)}
                active={this.state.active === 1}
              >
                1:00-2:00
              </button>
              <button
                style={this.styles}
                onClick={() => this.click(2)}
                active={this.state.active === 2}
                className="main-btn btn-border"
              >
                2:00-3:00
              </button>
              <br />
              <button
                style={this.styles}
                onClick={() => this.click(3)}
                active={this.state.active === 3}
                className="main-btn btn-border"
              >
                4:00-5:00
              </button>
              <button
                style={this.styles}
                onClick={() => this.click(4)}
                active={this.state.active === 4}
                className="main-btn btn-filled"
              >
                3:00-4:00
              </button>
              <br />
              <button
                style={this.styles}
                onClick={() => this.click(5)}
                active={this.state.active === 5}
                className="main-btn btn-filled"
              >
                5:00-6:00
              </button>
              <button
                style={this.styles}
                onClick={() => this.click(6)}
                active={this.state.active === 6}
                className="main-btn btn-border"
              >
                6:00-7:00
              </button>

              <br />
              <Link
                style={this.styles}
                className="main-btn btn-filled"
                to="/UserInfo"
              >
                Next
              </Link>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default mainProduct;
