import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Link } from "react-router-dom";

class mainProduct extends Component {
  state = {
    products: ["product 1", "product 2", "product 3"],
    selectedproduct: "select a product",
  };
  changedroplist = (e) => {
    this.setState({ selectedproduct: e.target.value });
  };
  setRedirect = () => {
    console.log("go to main");
    this.setState({
      redirect: true,
    });
  };
  render() {
    return (
      <section style={{ textAlign: "center" }}>
        <div className="nice-select">
          <select
            onChange={this.changedroplist}
            style={this.styles}
            id="mainProduct"
          >
            <option value="select a product">Select a product</option>
            {this.state.products.map((products) => (
              <option value={products}>{products}</option>
            ))}
          </select>
        </div>
        <h3>{this.state.selectedproduct}</h3>

        <select style={this.styles} id="No.people">
          <option value="">Select the number of people</option>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>

        <br />
        <input style={this.styles} type="date" id="date" />
        <br />

        <button style={this.styles}>1:00-2:00</button>
        <button style={this.styles}>2:00-3:00</button>
        <br />
        <button style={this.styles}>3:00-4:00</button>
        <button style={this.styles}>4:00-5:00</button>
        <br />
        <button style={this.styles}>5:00-6:00</button>
        <button style={this.styles}>6:00-7:00</button>
        <br />
        <Link className="main-btn btn-filled" to="/product">
          Next
        </Link>
      </section>
    );
  }
}

export default mainProduct;
