import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Link } from "react-router-dom";

class mainProduct extends Component {
  styles = {
    margin: "10px",
  };
  state = {
    products: ["product 1", "product 2", "product 3"],
    selectedproduct: "select a product",
  };
  changedroplist = (e) => {
    this.setState({ selectedproduct: e.target.value });
  };
  setRedirect = () => {
    console.log("go to main");
    this.setState({
      redirect: true,
    });
  };
  handleClick = () => {
    return { border: "5px, solid, blue," };
    console.log("hello");
  };
  render() {
    return (
      <section className="contact-part pt-115 pb-115">
        <div className="container">
          <div className="contact-form">
            <div className="input-group input-group-two left-icon mb-20">
              <div className="nice-select">
                <select onChange={this.changedroplist} id="mainProduct">
                  <option value="select a product">Select a product</option>
                  {this.state.products.map((products) => (
                    <option value={products}>{products}</option>
                  ))}
                </select>
              </div>
            </div>
            <h3 style={this.styles}>{this.state.selectedproduct}</h3>
            <div className="input-group input-group-two left-icon mb-20">
              <div className="nice-select">
                <select id="No.people">
                  <option value="">Select the number of people</option>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                </select>
              </div>
            </div>

            <br />
            <div className="input-group input-group-two left-icon mb-20">
              <div className="nice-select">
                <input style={this.styles} type="date" id="date" />
              </div>
            </div>
            <br />

            <button
              style={this.styles}
              className="main-btn btn-filled"
              onClick={this.handleClick}
            >
              1:00-2:00
            </button>
            <button style={this.styles} className="main-btn btn-border">
              2:00-3:00
            </button>
            <br />
            <button style={this.styles} className="main-btn btn-border">
              4:00-5:00
            </button>
            <button style={this.styles} className="main-btn btn-filled">
              3:00-4:00
            </button>
            <br />
            <button style={this.styles} className="main-btn btn-filled">
              5:00-6:00
            </button>
            <button style={this.styles} className="main-btn btn-border">
              6:00-7:00
            </button>
            <br />
            <Link
              style={this.styles}
              className="main-btn btn-filled"
              to="/Confirmation"
            >
              Next
            </Link>
          </div>
        </div>
      </section>
    );
  }
}

export default mainProduct;
